import React from 'react';
import { Tab } from '@headlessui/react';
import classNames from 'classnames';

function RenderedImages(props) {

    const getSelectedLocale = (e) => {
        props.onSelectedPlace(e);
    }

    return (
        <div className="container mx-auto px-4 px-8 ">
            <Tab.Group>
                <div className="grid sm:grid-cols-2 md:grid-cols-7">
                    {['United States', 'Latin America', 'Europe', 'Asia Pacific', 'Caribbean'].map((area, index) => (
                        <Tab key={area} value={index}>
                            {({ selected }) => (
                                <div id={area}
                                    className={classNames(
                                        selected
                                            ? 'inline-flex items-center border font-medium w-40 rounded-2xl pt-3 pb-3 mb-8 shadow-sm text-white bg-green-600 bg-opacity-70  hover:bg-green justify-center bg-green text-white'
                                            : 'inline-flex items-center font-medium w-40 rounded-2xl pt-3 pb-3 mb-8  hover:bg-green justify-center bg-white text-gray-700 hover:text-gray-900',
                                        'transition-colors duration-200 rounded-xl px-4 py-3'
                                    )} onClick={getSelectedLocale}
                                >
                                    {area}
                                </div>
                            )}
                        </Tab>
                    ))}
                </div>
                <Tab.Panels>
                    <div className="grid sm:grid-cols-2 md:grid-cols-4 gap-6 sm:gap-4">
                        {props.locations.filter(location => location.region === props.selectedLocale).map((location) => {
                            return (
                                <div key={location.id}>
                                    <a href={`https://app.outsite.co/book-now/search?where=${location.id}`}>
                                        <img src={location.wfContent.cityImage} alt="city"
                                            className="aspect-w-4 aspect-h-3 bg-cover bg-no-repeat object-cover w-full h-48 bg-center rounded-md"
                                            style={{ backgroundSize: "cover" }}
                                        />
                                        <div className="py-2 ">
                                            <h4 className="text-lg leading-6 font-medium text-gray-900">{location.wfContent?.name}</h4>
                                            <p className="text-sm leading-5 text-gray-600">{location.wfContent?.houseName}</p>
                                        </div>
                                    </a>
                                </div>
                            )
                        })}
                    </div>
                </Tab.Panels>
            </Tab.Group>
        </div>
    )
}

export default RenderedImages;
