import { useState } from 'react';
import { Disclosure, Listbox } from '@headlessui/react';
import { CheckIcon, ChevronUpIcon } from '@heroicons/react/solid';
import classNames from 'classnames';

const places = [
    { id: 1, name: 'Lisbon', unavailable: false },
    { id: 2, name: 'NYC', unavailable: false },
    { id: 3, name: 'Berlin', unavailable: false },
    { id: 4, name: 'London', unavailable: true },
    { id: 5, name: 'Rome', unavailable: false },
]

function groupBy(list, keyGetter) {
    const map = new Map();
    list.forEach((item) => {
        const key = keyGetter(item);
        const collection = map.get(key);
        if (!collection) {
            map.set(key, [item]);
        } else {
            collection.push(item);
        }
    });
    return map;
}

const placeGroups = groupBy(places, place => place.region);

function Dropdown({ locations }) {
    const [selectedPlace, setSelectedPlace] = useState(places[0])

    return (
        /* <Listbox value={selectedPlace} onChange={setSelectedPlace}>
            <Listbox.Button className="relative w-full bg-white border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 text-left cursor-default focus:outline-none focus:ring-1 focus:ring-outsite-green focus:border-outsite-green sm:text-sm">Select</Listbox.Button>
            <Listbox.Options>
                {places.map((place) => (
                    <Listbox.Option
                        key={place.id}
                        value={place}
                        disabled={place.unavailable}
                    >
                        {place.name}
                    </Listbox.Option>
                ))}
            </Listbox.Options>
        </Listbox> */

        <Listbox value={selectedPlace} onChange={setSelectedPlace}>
            {({ open }) => (
                <>
                    <div className="mt-1 relative">
                        <Listbox.Button
                            className={
                                'relative w-full bg-white border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 text-left cursor-default focus:outline-none focus:ring-1 focus:ring-outsite-green focus:border-outsite-green sm:text-sm'
                            }
                        >
                            <span className="w-full inline-flex truncate">
                                <span className="truncate">{selectedPlace?.wfContent?.name}</span>
                            </span>
                            <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                                <ChevronUpIcon
                                    className={`${open ? 'transform rotate-180' : ''
                                        } transition duration-500 h-5 w-5 text-gray-400`}
                                    aria-hidden="true"
                                />
                            </span>
                        </Listbox.Button>
                        <Listbox.Options
                            static
                            className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm"
                        >
                            {Object.keys(placeGroups).map((group) => (
                                <Disclosure as="div" className="mt-2">
                                    {({ open }) => (
                                        <>
                                            <Disclosure.Button className="flex justify-between w-full px-4 py-2 text-lg text-gray-900 text-left ">
                                                <span>{group}</span>
                                                <ChevronUpIcon className={`${open ? 'transform rotate-180' : ''} w-5 h-5`} />
                                            </Disclosure.Button>
                                            <Disclosure.Panel className="px-4 pb-2 text-gray-500">
                                                {locations.map((location) => (
                                                    <Listbox.Option
                                                        key={location.id}
                                                        className={({ active, selected }) =>
                                                            classNames(
                                                                active ? 'cursor-pointer bg-gray-100' : 'cursor-default',
                                                                'select-none relative py-1 pl-8 pr-4'
                                                            )
                                                        }
                                                        value={location.id}
                                                    >
                                                        {({ selected, active }) => (
                                                            <>
                                                                <div className="flex flex-col">
                                                                    <span
                                                                        className={classNames(
                                                                            'text-gray-900 text-base truncate transition-colors duration-200'
                                                                        )}
                                                                    >
                                                                        {location.wfContent?.name}
                                                                    </span>
                                                                    <span
                                                                        className={classNames(
                                                                            'text-gray-600 text-xxs tracking-widest uppercase truncate'
                                                                        )}
                                                                    >
                                                                        {location.wfContent?.houseName}
                                                                    </span>
                                                                </div>

                                                                {selected ? (
                                                                    <span
                                                                        className={classNames(
                                                                            'absolute inset-y-0 left-0 flex items-center pl-1 text-outsite-green'
                                                                        )}
                                                                    >
                                                                        <CheckIcon className="h-5 w-5" aria-hidden="true" />
                                                                    </span>
                                                                ) : null}
                                                            </>
                                                        )}
                                                    </Listbox.Option>
                                                ))}
                                            </Disclosure.Panel>
                                        </>
                                    )}
                                </Disclosure>

                            ))}
                        </Listbox.Options>
                    </div>
                </>
            )}
        </Listbox>
    )
}

export default Dropdown;
