import { useState } from 'react';
import { Listbox, Transition } from '@headlessui/react';
import { CheckIcon, ChevronUpIcon } from '@heroicons/react/solid';


function Guests() {
    const [selectedGuest, setSelectedGuest] = useState(1);
    const [guestText, setGuestText] = useState("One guest");

    const guestHandler = (e) => {
        e.preventDefault();
        setSelectedGuest(e.target.id);
        setGuestText(e.target.value);
    }

    return (
        <Listbox value={guestText}>
            {({ open }) => (
                <div className="relative mt-1">
                    <Listbox.Button
                        className={
                            'relative w-full appearance-none block w-full text-left px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-outsite-green focus:border-outsite-green disabled:text-gray-400 sm:text-sm'
                        }
                    >
                        <span className="block truncate">{guestText ? guestText : 'Select'}</span>
                        <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                            <ChevronUpIcon
                                className={`${open ? 'transform rotate-180' : ''
                                    } transition duration-500 h-5 w-5 text-gray-400`}
                                aria-hidden="true"
                            />
                        </span>
                    </Listbox.Button>
                    <Transition
                        show={open}
                        leave="transition-opacity ease-in duration-100"
                        leaveFrom="opacity-100"
                        leaveTo="opacity-0"
                    >
                        <Listbox.Options className="absolute z-10 w-full py-1 mt-1 overflow-auto text-base bg-white rounded-md shadow-lg max-h-60 ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm">
                            <Listbox.Option
                                id={1}
                                value={"One guest"}
                                onClick={guestHandler}
                                className={({ active }) =>
                                    `${active ? 'bg-gray-100' : ''
                                    } text-gray-900 cursor-default select-none relative py-2 pl-10 pr-4`
                                }
                            >
                                {({ selected, active }) => (
                                    <>
                                        <span
                                            className={`${selected ? 'font-medium' : 'font-normal'} block truncate`}
                                        >
                                            One guest
                                </span>
                                        {selected ? (
                                            <span
                                                className={`${active ? 'text-outsite-green' : 'text-outsite-green'}
                        absolute inset-y-0 left-0 flex items-center pl-3`}
                                            >
                                                <CheckIcon className="w-5 h-5" aria-hidden="true" />
                                            </span>
                                        ) : null}
                                    </>
                                )}
                            </Listbox.Option>




                            <option
                                id={2}
                                value={"Two guests"}
                                onClick={guestHandler}
                            >
                                Two guests
                                </option>
                        </Listbox.Options>
                    </Transition>
                </div>
            )}
        </Listbox>
    )
}

export default Guests;
