import React, { useState, useEffect } from 'react';
import DatePicker from 'react-date-picker';
import Guests from './Guests';
import Places from './Places';
import Hero from './images/Hero.jpeg';
import axios from 'axios';
import { Tab } from '@headlessui/react';
import classNames from 'classnames';

function LocationsPage() {

    const [locations, setLocations] = useState([]);
    const [region, setRegion] = useState("United States");

    useEffect(() => {
        const fetchLocations = async () => {
            const { data } = await axios.get('http://localhost:5000');
            console.log(data);
            setLocations(data);
        }
        fetchLocations();
    }, [])

    const handleTab = (area) => {
        setRegion(area);
    }

    return (
        <div className="">
            <div className="px-4 sm:px-6 lg:px-0">
                <div className="bg-contain max-w-full h-96 bg-center bg-no-repeat" style={{ backgroundImage: `url(${Hero})`, backgroundSize: 'cover' }}>
                </div>
                <div className="relative container mx-auto px-4 z-10">
                    <div className="-mt-56 sm:-mt-32 lg:-mt-24 mb-10 ">
                        <div className="text-3xl leading-9 text-white font-semibold mb-4 text-left">
                            Work anywhere. Live differently.
                    </div>
                    </div>
                    <div className="w-full px-3 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus-within:outline-none focus-within:ring-1 focus-within:ring-outsite-green focus-within:border-outsite-green disabled:text-gray-400 bg-white filter drop-shadow-md rounded-md px-4 py-3 sm:px-8">
                        <div className="grid sm:grid-cols-2 lg:grid-cols-6 gap-2 items-end">
                            <div className="lg:col-span-2">
                                <div className="text-sm text-left leading-5 font-medium text-gray-700">Space</div>
                                <Places></Places>
                            </div>
                            <div className="lg:col-span-2">
                                <div className="text-sm text-left leading-5 font-medium text-gray-700">Dates</div>
                                <DatePicker className="date-range-picker w-full px-3 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus-within:outline-none focus-within:ring-1 focus-within:ring-outsite-green focus-within:border-outsite-green disabled:text-gray-400"></DatePicker>
                            </div>
                            <div className="">
                                <div className="text-sm text-left leading-5 font-medium text-gray-700">Guests</div>
                                <Guests></Guests>
                            </div>
                            <div className="h-10">
                                <button type='button' className='inline-flex items-center border font-medium rounded pt-2 pb-2 shadow-sm text-white bg-green-600 bg-opacity-70  hover:bg-outsite-green w-full justify-center'>
                                    Search
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 my-16">
                <div className="my-16">
                    <div className="grid sm:grid-cols-2 gap-8">
                        <div className="text-left">
                            <div className="text-3xl leading-9 font-semibold mb-4 text-gray-900">
                                Book confidently in a fast-changing world.
                            </div>
                            <button type='button' className='inline-flex border font-medium rounded pt-2 pb-2 shadow-sm text-white bg-green-600 bg-opacity-70 px-2.5 py-1.5 text-xs w-50 text-left hover:bg-outsite-green justify-center'>
                                Read our COVID-19 FAQ
                            </button>
                        </div>
                        <div className="text-gray-600 text-left text-sm leading-5">
                            Weâ€™ve upgraded our professional space cleaning, implemented contactless check-in, and improved booking flexibility and cancellation policies to help our Members travel with confidence.
                        </div>
                    </div>
                </div>
                <div className="my-16">
                </div>
                <div className="my-16">
                    <div>
                        <div className="flex items-center justify-between mb-3">
                            <h3 className="text-3xl leading-9 font-semibold text-gray-900">
                                Explore Locations
                            </h3>
                            <button type="button" className="inline-flex items-center border border-green text-green-500 font-medium rounded shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-outsite-green disabled:opacity-50 px-4 py-2 text-sm bg-white hover:bg-outsite-green hover:text-white">
                                See all locations
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 my-16">
                <Tab.Group>
                    <div className="grid sm:grid-cols-2 md:grid-cols-7">
                        {['United States', 'Latin America', 'Europe', 'Asia Pacific', 'Caribbean'].map((area, index) => (
                            <Tab key={area} value={index}>
                                {({ selected }) => (
                                    <button
                                        className={classNames(
                                            selected
                                                ? 'inline-flex items-center border font-medium w-40 rounded-2xl pt-3 pb-3 mb-8 shadow-sm text-white bg-green-600 bg-opacity-70  hover:bg-outsite-green justify-center bg-outsite-green text-white'
                                                : 'inline-flex items-center font-medium w-40 rounded-2xl pt-3 pb-3 mb-8  hover:bg-outsite-green justify-center bg-white text-gray-700 hover:text-gray-900',
                                            'transition-colors duration-200 rounded-xl px-4 py-3'
                                        )} onClick={handleTab(area)}
                                    >
                                        {area}
                                    </button>
                                )}
                            </Tab>
                        ))}
                    </div>
                    <div>The region is:</div>{region}
                    <Tab.Panels>
                        <div className="grid sm:grid-cols-2 md:grid-cols-4 gap-6 sm:gap-4">
                            {locations.map((location) => {
                                return (
                                    <div key={location.id}>
                                        <a href={`https://app.outsite.co/book-now/search?where=${location.id}`}>
                                            <img src={location.wfContent.cityImage} alt="city"
                                                className="aspect-w-4 aspect-h-3 bg-cover bg-no-repeat object-cover w-full h-48 bg-center rounded-md"
                                                style={{ backgroundSize: "cover" }}
                                            />
                                            <div className="py-2 ">
                                                <h4 className="text-lg leading-6 font-medium text-gray-900">{location.wfContent?.name}</h4>
                                                <p className="text-sm leading-5 text-gray-600">{location.wfContent?.houseName}</p>
                                            </div>
                                        </a>
                                    </div>
                                )
                            })}
                        </div>
                    </Tab.Panels>
                </Tab.Group>
            </div>
        </div>
    );
}

export default LocationsPage;
