import React, { useState, useEffect } from 'react';
import DatePicker from "react-datepicker";
import Guests from './Guests';
import Places from './Places';
import Covid from './Covid';
import Hero from './images/Hero.jpeg';
import axios from 'axios';
import "react-datepicker/dist/react-datepicker.css";
import RenderedImages from './RenderedImages';

function LocationsPage() {

    const [locations, setLocations] = useState([]);
    const [selectedLocale, setSelectedLocale] = useState("United States");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [selectedPlace, setSelectedPlace] = useState('lb');
    const [selectedPlaceName, setSelectedPlaceName] = useState("Lisbon - Cais do Sodre");
    const [selectedNumber, setSelectedNumber] = useState(1);
    const [guestText, setGuestText] = useState("One guest");

    useEffect(() => {
        const fetchLocations = async () => {
            const { data } = await axios.get('http://localhost:5000');
            console.log(data.locations);
            setLocations(data.locations);
        }
        fetchLocations();
    }, [])

    const handleSelectedPlace = (e) => {
        setSelectedPlace(e.id);
        setSelectedPlaceName(e.wfContent?.name);
    }

    const handleSelectedLocale = (e) => {
        setSelectedLocale(e.target.id);
    }

    const handleSelectedGuests = (e) => {
        setSelectedNumber(e.target.id);
        setGuestText(e.target.value);
    }

    return (
        <div className="">
            <div className="px-0">
                <div className="h-96 bg-center" style={{ backgroundImage: `url(${Hero})`, backgroundSize: 'cover' }}>
                </div>
                <div className="container mx-auto px-8">
                    <div className="-mt-28 mb-10 ">
                        <div className="text-white font-semibold text-3xl mb-4 text-left">
                            Work anywhere. Live differently.
                        </div>
                    </div>
                    <div className="w-full px-3 px-8 py-3 border border-gray-300 shadow-sm bg-white filter drop-shadow-md">
                        <div className="grid grid-cols-6 gap-2 items-end">
                            <div className="col-span-2">
                                <div className="text-left text-sm font-medium text-gray-700">Space</div>
                                <Places locations={locations} selectedPlace={selectedPlace} selectedPlaceName={selectedPlaceName} onSelectedPlace={handleSelectedPlace} ></Places>
                            </div>
                            <div className="col-span-2">
                                <div className="text-left text-sm font-medium text-gray-700">Dates</div>
                                <div className="flex w-full border border-gray-300 py-1.5">
                                    <DatePicker selected={startDate} onChange={(date) => setStartDate(date)} className=""></DatePicker>
                                    <DatePicker selected={endDate} onChange={(date) => setEndDate(date)} className=""></DatePicker>
                                </div>
                            </div>
                            <div className="">
                                <div className="text-left text-sm font-medium text-gray-700">Guests</div>
                                <Guests selectedNumber={selectedNumber} guestText={guestText} onSelectedGuests={handleSelectedGuests}></Guests>
                            </div>
                            <div className="h-10">
                                <a href={`https://app.outsite.co/book-now/search?where=${selectedPlace}&guests=${selectedNumber}&startDate=${startDate.toISOString().slice(0, 10)}&endDate=${endDate.toISOString().slice(0, 10)}`}>
                                    <button type='button' className='inline-flex items-center border font-medium rounded pt-2 pb-2 shadow-sm text-white bg-green-600 bg-opacity-70  hover:bg-green w-full justify-center'>
                                        Search
                                    </button>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Covid></Covid>
            <RenderedImages locations={locations} selectedLocale={selectedLocale} onSelectedPlace={handleSelectedLocale}></RenderedImages>
        </div>
    );
}

export default LocationsPage;
