import { useState } from 'react';
import { Listbox, Transition } from '@headlessui/react';
import { ChevronUpIcon } from '@heroicons/react/solid';

function Guests() {
    const [selectedGuest, setSelectedGuest] = useState("One guest");

    const guests = [
        { id: 1, name: 'One Guest' },
        { id: 2, name: 'Two Guests' },
    ]

    return (
        <Listbox value={selectedGuest}>
            {({ open }) => (
                <div className="relative mt-1">
                    <Listbox.Button
                        className={
                            'relative w-full appearance-none block w-full text-left px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-outsite-green focus:border-outsite-green disabled:text-gray-400 sm:text-sm'
                        }
                    >
                        <span className="block truncate">{selectedGuest ? selectedGuest : 'Select'}</span>
                        <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                            <ChevronUpIcon
                                className={`${open ? 'transform rotate-180' : ''
                                    } transition duration-500 h-5 w-5 text-gray-400`}
                                aria-hidden="true"
                            />
                        </span>
                    </Listbox.Button>
                    <Transition
                        show={open}
                        leave="transition-opacity ease-in duration-100"
                        leaveFrom="opacity-100"
                        leaveTo="opacity-0"
                    >
                        <Listbox.Options className="absolute z-10 w-full py-1 mt-1 overflow-auto text-base bg-white rounded-md shadow-lg max-h-60 ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm">
                            {guests.map((guest) => (
                                <Listbox.Option
                                    key={guest.id}
                                    value={guest.name}
                                >
                                    <div onClick={(e) => setSelectedGuest(e.target)}>
                                        {guest.name}
                                    </div>
                                </Listbox.Option>
                            ))}
                        </Listbox.Options>
                    </Transition>
                </div>
            )}
        </Listbox>
    )
}

export default Guests;
